import { Component } from '@angular/core';
import { environment } from '../../environments/environment';
import { HttpClient } from '@angular/common/http';
import { Movie } from '../__models/Movie';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-search-movie',
  templateUrl: './search-movie.component.html',
  styleUrls: ['./search-movie.component.css']
})
export class SearchMovieComponent {
  title!: string;
  baseUrl = environment.apiUrl;
  movies!: Movie[];
  selectedMovie: Movie | null;
  constructor(private http: HttpClient) { this.selectedMovie = null;}

  onSearchMovie() {
    console.log('Searching for movie:', this.title);

    const url = `${this.baseUrl}movie?title=${encodeURIComponent(this.title)}`;

    this.http.get<Movie[]>(url).pipe(
      map((response: Movie[]) => {
        return response; // Assuming the API returns the list of movies directly
      })
    ).subscribe(
      (movieList: Movie[]) => {
        this.movies = movieList;
        console.log('Movie List:', movieList);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }


  toggleExtendedInfo(movie: Movie) {
    if (this.selectedMovie === movie) {
      this.selectedMovie = null; // Deselect the movie if it's already selected
    } else {
      this.selectedMovie = movie; // Select the clicked movie
    }
  }


}

<div class="container mt-4">
    <h2>Search Movies</h2>

    <form (ngSubmit)="onSearchMovie()" class="mt-3">
        <div class="mb-3">
            <label for="titleInput" class="form-label">Enter movie title</label>
            <input type="text" [(ngModel)]="title" name="title" id="titleInput" class="form-control" />
        </div>
        <button type="submit" class="btn btn-primary">Search</button>
    </form>


    <div id="searchResults" class="mt-3">
        <div *ngFor="let movie of movies">
            <!-- Display movie details here -->
            <h3>{{ movie.title }}</h3>
<!-- 
            <img class="styled-image"
                 src="{{ movie.poster !== 'N/A' ? movie.poster : './assets/Generic.jpeg' }}"
                 alt="Nicely Formatted Image">
 -->
            <div class="movie-container">
                <img class="styled-image"
                     src="{{ movie.poster !== 'N/A' ? movie.poster : './assets/Generic.jpeg' }}"
                     alt="Nicely Formatted Image">

                <div class="expand-icon position-absolute start-50 translate-middle" (click)="toggleExtendedInfo(movie)">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-chevron-right" viewBox="0 0 16 16">
                        <path d="M4.646 12.354a.5.5 0 0 1 0-.708L9.293 8 4.646 3.354a.5.5 0 1 1 .708-.708l5 5a.5.5 0 0 1 0 .708l-5 5a.5.5 0 0 1-.708 0z" />
                    </svg>
                </div>


                <div class="extended-info" *ngIf="selectedMovie === movie">
                    <p>Description: {{ selectedMovie.runtime }}</p>
                    <p>Language: {{ selectedMovie.language }}</p>
                    <p>Country: {{ selectedMovie.country }}</p>
                    <p>Released Date: {{ selectedMovie.released }}</p>
                    <p>Runtime: {{ selectedMovie.runtime }}</p>
                    <!-- Add more movie details as needed -->
                </div>
            </div>

        </div>
    </div>
</div>
