import { Component } from '@angular/core';
import { environment } from '../../environments/environment';
import { HttpClient } from '@angular/common/http';
import { Movie } from '../__models/Movie';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-search-movie',
  templateUrl: './search-movie.component.html',
  styleUrls: ['./search-movie.component.css']
})
export class SearchMovieComponent {
  title: string | undefined;
  baseUrl = environment.apiUrl;
  constructor(private http: HttpClient) { }

  onSearchMovie() {

    console.log('Searching for movie:', this.title);
    this.http.post<Movie>(this.baseUrl + 'movie/search', { title: this.title }).pipe(
      map((response: Movie) => {
        return response; // Assuming the API returns the list of movies directly
      })
    ).subscribe(
      (movieList: Movie) => {
        console.log('Movie List:', movieList);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }
}

<div class="container mt-4">
    <h2>Search Movies</h2>

    <form (ngSubmit)="onSearchMovie()" class="mt-3">
        <div class="mb-3">
            <label for="titleInput" class="form-label">Enter movie title</label>
            <input type="text" [(ngModel)]="title" name="title" id="titleInput" class="form-control" />
        </div>
        <button type="submit" class="btn btn-primary">Search</button>
    </form>

    <div id="searchResults" class="mt-3"></div>
</div>
