import { Component } from '@angular/core';
import { environment } from '../../environments/environment';
import { HttpClient } from '@angular/common/http';
import { Movie } from '../__models/Movie';
import { map } from 'rxjs/operators';
import { MovieService } from '../_services/MovieService';

@Component({
  selector: 'app-search-movie',
  templateUrl: './search-movie.component.html',
  styleUrls: ['./search-movie.component.css']
})
export class SearchMovieComponent {
  title!: string;

  movies!: Movie[];
  selectedMovie!: Movie;
  baseUrl = environment.apiUrl;
  
  constructor(private http:HttpClient, private movieService: MovieService) { }

  onSearchMovie() {
    console.log('Searching for movie:', this.title);
    const url = `${this.baseUrl}movie?title=${encodeURIComponent(this.title)}`;

    this.http.get<Movie[]>(url).subscribe(
      (movieList: Movie[]) => {
        this.movies = movieList; // Assign the retrieved movie list to the property
        console.log('Movie List:', this.movies);
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  toggleExtendedInfo(movie: Movie) {
    this.toggleExtendedInfo(movie);
  }

  isMovieSelected(movie: Movie) {
    return this.selectedMovie === movie;
  }


}

<div class="container mt-4">
    <h2>Search Movies</h2>

    <form (ngSubmit)="onSearchMovie()" class="mt-3">
        <div class="mb-3">
            <label for="titleInput" class="form-label">Enter movie title</label>
            <input type="text" [(ngModel)]="title" name="title" id="titleInput" class="form-control" />
        </div>
        <button type="submit" class="btn btn-primary">Search</button>
    </form>


    <div id="searchResults" class="mt-3">
        <div *ngFor="let movie of movies">
            <h3>{{ movie.title }}</h3>
            <div class="movie-container">

                    <img class="styled-image"
                         src="{{ movie.poster !== 'N/A' ? movie.poster : './assets/Generic.jpeg' }}"
                         (click)="toggleExtendedInfo(movie)"
                         alt="Nicely Formatted Image">


                <div class="extended-info" *ngIf="isMovieSelected(movie)">
                    <p>Genre: {{ selectedMovie.genre }}</p>
                    <p>Language: {{ selectedMovie.language }}</p>
                    <p>Country: {{ selectedMovie.country }}</p>
                    <p>Year: {{ selectedMovie.year }}</p>
                    <p>Released Date: {{ selectedMovie.released }}</p>
                    <p>Runtime: {{ selectedMovie.runtime }}</p>
                    <p>IMDB Rating: {{ selectedMovie.imdbRating }}</p>
                    <p>Director: {{ selectedMovie.director }}</p>
                    <p>Type: {{ selectedMovie.type }}</p>
                </div>
            </div>

        </div>
    </div>
</div>
