import { Component } from '@angular/core';

@Component({
  selector: 'app-search-movie',
  templateUrl: './search-movie.component.html',
  styleUrls: ['./search-movie.component.css']
})
export class SearchMovieComponent {
  title: string|undefined;

  constructor() { }

  onSearchMovie() {

    console.log('Searching for movie:', this.title);
    // You can make API calls here to search for movies based on the entered title
    // Update the search results div accordingly
  }
}

<div class="container mt-4">
    <h2>Search Movies</h2>

    <form (ngSubmit)="onSearchMovie()" class="mt-3">
        <div class="mb-3">
            <label for="titleInput" class="form-label">Enter movie title</label>
            <input type="text" [(ngModel)]="title" name="title" id="titleInput" class="form-control" />
        </div>
        <button type="submit" class="btn btn-primary">Search</button>
    </form>

    <div id="searchResults" class="mt-3"></div>
</div>
